import React from 'react';

export default function Question({title, answer}) {

    const articleStyle = {
        border: "1px solid #333",
        margin: "1rem"
    }

    return (
        <article style={articleStyle}>
            <header>
            <h4>{title} <i class="fa fa-chevron-down" aria-hidden="true"></i></h4>
            </header>
            <p>{answer}</p>
        </article>
    )
}
